package MYPACKAGE;

public class INCDEC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5,b;
b=a++;
System.out.println("value of b is:"+b);
b=--a;
System.out.println("value of b is:"+b);
b=a--;
System.out.println("value of b is:"+b);
a=a--;
System.out.println("value of b is:"+b);
	}

}
