package MYPACKAGE;

public class RELATIONALOPERATOR {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=10,b=20;
     boolean res;
     res=a==b;
     System.out.println("a==b :"+res);
     res=a!=b;
     System.out.println("a!=b :"+res);
     res=a>=b;
     System.out.println("a>=b :"+res);
     res=a<=b;
     System.out.println("a<=b :"+res);
	}

}
